// Determinar status do agente baseado no PostgreSQL
const dbStatus = $input.first().json;

console.log('üîç DB Status:', dbStatus);

let status = 'stopped';
let message = 'Agente parado';
let executionId = null;
let iniciadoEm = null;
let paradoEm = null;
let finalizadoEm = null;
let erroEm = null;
let mensagemErro = null;
let duracaoSegundos = null;
let usuarioNome = null;
let usuarioEmail = null;

// Verificar status no banco de dados
if (dbStatus && dbStatus.status) {
  status = dbStatus.status;
  message = status === 'running' ? 'Agente rodando' : 'Agente parado';
  executionId = dbStatus.execution_id;
  iniciadoEm = dbStatus.iniciado_em;
  paradoEm = dbStatus.parado_em;
  finalizadoEm = dbStatus.finalizado_em;
  erroEm = dbStatus.erro_em;
  mensagemErro = dbStatus.mensagem_erro;
  duracaoSegundos = dbStatus.duracao_segundos;
  usuarioNome = dbStatus.usuario_nome;
  usuarioEmail = dbStatus.usuario_email;
  console.log('‚úÖ Status do DB:', status);
} else {
  console.log('‚ö†Ô∏è Nenhum status encontrado no DB, usando padr√£o: stopped');
}

return {
  json: {
    status: status,
    message: message,
    executionId: executionId,
    workflowId: $('Normalizar Dados').item.json.workflowId,
    iniciadoEm: iniciadoEm,
    paradoEm: paradoEm,
    finalizadoEm: finalizadoEm,
    erroEm: erroEm,
    mensagemErro: mensagemErro,
    duracaoSegundos: duracaoSegundos,
    usuarioNome: usuarioNome,
    usuarioEmail: usuarioEmail,
    timestamp: new Date().toISOString()
  }
};
