// Processar perfis do banco
const perfis = $input.all();

console.log('ðŸ“‹ Perfis encontrados:', perfis.length);

// Mapear para o formato esperado pelo frontend
const perfisFormatados = perfis.map(item => {
  const data = item.json;

  return {
    id: parseInt(data.id),
    nome_perfil: data.nome_perfil,
    descricao: data.descricao,
    created_at: data.created_at,
    updated_at: data.updated_at,
    permissoes: data.permissoes || []
  };
});

console.log('âœ… Perfis formatados:', perfisFormatados.length);
